// Predefined symbols for trading engine
const symbols = ['AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA', 'META', 'NVDA', 'JPM', 'BAC', 'WFC', 'XOM', 'CVX', 'MCD', 'NKE', 'COST'];
return symbols.map(s => ({ symbol: s }));

// Extract and normalize price data from Alpha Vantage
const data = $input.all();
return data.map(item => {
  const symbol = item.json.symbol;
  const response = item.json.body || item.json;

  // Alpha Vantage returns time series data, get the most recent day
  const timeSeries = response['Time Series (Daily)'] || {};
  const dates = Object.keys(timeSeries).sort().reverse();

  if (dates.length === 0) {
    return null;
  }

  const latestDate = dates[0];
  const priceData = timeSeries[latestDate];

  return {
    symbol: symbol,
    timestamp: new Date().toISOString(),
    close: parseFloat(priceData['4. close']) || 0,
    volume: parseInt(priceData['5. volume']) || 0,
    open: parseFloat(priceData['1. open']) || 0,
    high: parseFloat(priceData['2. high']) || 0,
    low: parseFloat(priceData['3. low']) || 0,
    ingestion_run_id: '{{$json.run_id}}'
  };
}).filter(d => d && d.close > 0);
