const portfolioRaw = $('Load Portfolio Summary').all() || [];
const topMoversRaw = $('Load Top Movers').all() || [];
const signalsRaw = $('Load Active Signals').all() || [];
const strategiesRaw = $('Load Strategy Breakdown').all() || [];
const tradesRaw = $('Load Recent Trades').all() || [];
const pricesRaw = $('Load Volatility Data').all() || [];

const portfolio = portfolioRaw[0]?.json || {};
const topMovers = topMoversRaw.map(m => m.json);
const signals = signalsRaw.map(s => s.json);
const strategies = strategiesRaw.map(s => s.json);
const trades = tradesRaw.map(t => t.json);
const prices = pricesRaw.map(p => p.json);

// Calculate volatility by symbol
const volatilityMap = {};
const priceMap = {};
prices.forEach(p => {
  if (!priceMap[p.symbol]) priceMap[p.symbol] = [];
  priceMap[p.symbol].push(parseFloat(p.close));
});

for (const symbol in priceMap) {
  const closes = priceMap[symbol];
  if (closes.length < 2) continue;
  const returns = [];
  for (let i = 1; i < closes.length; i++) {
    returns.push((closes[i] - closes[i-1]) / closes[i-1]);
  }
  const mean = returns.reduce((a,b) => a+b) / returns.length;
  const variance = returns.reduce((sq, r) => sq + Math.pow(r - mean, 2), 0) / returns.length;
  volatilityMap[symbol] = Math.sqrt(variance) * 100;
}

return {
  metadata: {
    timestamp: new Date().toISOString(),
    version: "1.0"
  },
  portfolio: {
    total_value: portfolio.total_portfolio_value || 100000,
    unrealized_pnl: portfolio.total_unrealized_pnl || 0,
    realized_pnl: portfolio.realized_pnl || 0,
    sharpe_ratio: portfolio.sharpe_ratio || 0,
    max_drawdown: portfolio.max_drawdown || 0,
    win_rate: portfolio.win_rate || 0,
    cagr: portfolio.cagr || 0,
    num_positions: portfolio.num_positions || 0,
    num_trades: portfolio.num_trades || 0
  },
  top_movers: topMovers.slice(0, 5).map(m => ({
    symbol: m.symbol,
    pnl: m.unrealized_pnl || 0,
    pnl_pct: ((m.unrealized_pnl || 0) / (m.position_value || 1)) * 100,
    quantity: m.quantity,
    entry_price: m.entry_price,
    current_price: m.current_price,
    strategy: m.strategy_type
  })),
  signals_summary: {
    total_active: signals.length,
    by_strategy: {
      mean_reversion: signals.filter(s => s.strategy_type === 'mean_reversion').length,
      momentum: signals.filter(s => s.strategy_type === 'momentum').length,
      statistical_arbitrage: signals.filter(s => s.strategy_type === 'statistical_arbitrage').length
    },
    latest: signals.slice(0, 10).map(s => ({
      symbol: s.symbol,
      strategy: s.strategy_type,
      signal: s.signal === 1 ? 'LONG' : 'SHORT',
      strength: s.signal_strength
    }))
  },
  strategy_breakdown: strategies.map(s => ({
    strategy: s.strategy_type,
    positions: s.num_positions,
    value: s.total_position_value,
    unrealized_pnl: s.total_unrealized_pnl
  })),
  recent_trades: trades.slice(0, 10).map(t => ({
    symbol: t.symbol,
    side: t.side,
    quantity: t.quantity,
    price: t.price,
    strategy: t.strategy_type,
    timestamp: t.timestamp
  })),
  volatility: {
    avg: Object.keys(volatilityMap).length > 0 ? Object.values(volatilityMap).reduce((a,b) => a+b) / Object.keys(volatilityMap).length : 0,
    by_symbol: Object.keys(volatilityMap).slice(0, 10).map(s => ({ symbol: s, vol: volatilityMap[s] }))
  }
};
